try {
    let eiraResponse = '';
    let needsHumanInput = false;
    let finalState: BaseMessage[] | null = null;

    // Use the stream to process events one by one
    for await (const event of eira.stream(nextUserInput, chatHistory)) {
        if (event.content) {
            const content = Array.isArray(event.content) ? event.content.join('\n') : event.content;
            process.stdout.write(content); // Print content as it streams
            eiraResponse += content;
        }

        // Check for the human-in-the-loop tool call
        if (event instanceof AIMessage && event.tool_calls?.some(tc => tc.name === 'askHumanForHelpTool' || tc.name === 'humanConfirmationTool')) {
            needsHumanInput = true;
        }

        // Keep track of the latest state
        finalState = event.state;
    }

    // Update chat history with the final state from the run
    if (finalState) {
        chatHistory = finalState;
        await saveMemory(memoryFilePath, chatHistory);
    }

    console.log(); // Newline after streaming is complete

    // Now, after the stream is finished, we prompt for the next input
    if (needsHumanInput) {
        // The prompt is already part of the streamed response
        nextUserInput = await rl.question('Marius: ');
    } else {
        // Normal turn, prompt for the next input
        nextUserInput = await rl.question('\nMarius: ');
    }

} catch (error) {
    console.error('\nError during agent execution:', error);
    // It's crucial to still prompt for the next turn even after an error
    nextUserInput = await rl.question('An error occurred. Please try again or type \'exit\'.\nMarius: ');
}
