// src/tools/gitTools.ts
import { Tool } from '@langchain/core/tools';
import { z } from 'zod';
import { exec } from 'child_process';
import { promisify } from 'util';

const execAsync = promisify(exec);

class GitStatusTool extends Tool {
  name = 'gitStatusTool';
  description = 'Checks the current status of the Git repository (untracked files, modified files, current branch).';

  schema = z.object({});

  async _call(input: any): Promise<string> {
    try {
      const { stdout, stderr } = await execAsync('git status');
      if (stderr) {
        return `Error executing git status: ${stderr}`;
      }
      return stdout;
    } catch (error: any) {
      return `Error executing git status: ${error.message}`;
    }
  }
}

class GitDiffTool extends Tool {
  name = 'gitDiffTool';
  description = 'Shows the changes between the working directory and the staging area.';

  schema = z.object({
    filePath: z.string().optional(),
  });

  async _call(input: { filePath?: string }): Promise<string> {
    const command = input.filePath ? `git diff ${input.filePath}` : 'git diff';
    try {
      const { stdout, stderr } = await execAsync(command);
      if (stderr) {
        return `Error executing git diff: ${stderr}`;
      }
      return stdout;
    } catch (error: any) {
      // Git diff returns a non-zero exit code if there are differences, which can cause an error.
      // We return stdout in this case as it contains the diff information.
      if (error.stdout) {
        return error.stdout;
      }
      return `Error executing git diff: ${error.message}`;
    }
  }
}

class GitAddTool extends Tool {
  name = 'gitAddTool';
  description = 'Stages one or more files for the next commit.';

  schema = z.object({
    files: z.array(z.string()),
  });

  async _call(input: { files: string[] }): Promise<string> {
    if (!input.files || input.files.length === 0) {
      return 'Error: No files provided to stage.';
    }
    const command = `git add ${input.files.join(' ')}`;
    try {
      const { stderr } = await execAsync(command);
      if (stderr) {
        return `Error executing git add: ${stderr}`;
      }
      return `Successfully staged files: ${input.files.join(', ')}`;
    } catch (error: any) {
      return `Error executing git add: ${error.message}`;
    }
  }
}

class GitCommitTool extends Tool {
  name = 'gitCommitTool';
  description = 'Creates a new commit with the staged changes.';

  schema = z.object({
    message: z.string(),
  });

  async _call(input: { message: string }): Promise<string> {
    if (!input.message) {
      return 'Error: No commit message provided.';
    }
    // Escape double quotes in the message
    const escapedMessage = input.message.replace(/"/g, '\\"');
    const command = `git commit -m "${escapedMessage}"`;
    try {
      const { stdout, stderr } = await execAsync(command);
      if (stderr) {
        return `Error executing git commit: ${stderr}`;
      }
      return `Successfully created commit: ${stdout}`;
    } catch (error: any) {
      return `Error executing git commit: ${error.message}`;
    }
  }
}

class GitCreateBranchTool extends Tool {
  name = 'gitCreateBranchTool';
  description = 'Creates a new branch from the current HEAD.';

  schema = z.object({
    branchName: z.string(),
  });

  async _call(input: { branchName: string }): Promise<string> {
    if (!input.branchName) {
      return 'Error: No branch name provided.';
    }
    const command = `git checkout -b ${input.branchName}`;
    try {
      const { stderr } = await execAsync(command);
      if (stderr) {
        return `Error creating new branch: ${stderr}`;
      }
      return `Successfully created and switched to new branch: ${input.branchName}`;
    } catch (error: any) {
      return `Error creating new branch: ${error.message}`;
    }
  }
}

class GitPushTool extends Tool {
  name = 'gitPushTool';
  description = 'Pushes the current branch to the remote repository.';

  schema = z.object({});

  async _call(input: any): Promise<string> {
    try {
      const { stdout, stderr } = await execAsync('git push');
      if (stderr) {
        // stderr can contain progress information, so we check for specific error keywords
        if (stderr.toLowerCase().includes('error') || stderr.toLowerCase().includes('fatal')) {
            return `Error executing git push: ${stderr}`;
        }
      }
      return `Successfully pushed branch to remote. Output: ${stdout} ${stderr}`;
    } catch (error: any) {
      return `Error executing git push: ${error.message}`;
    }
  }
}

export const gitStatusTool = new GitStatusTool();
export const gitDiffTool = new GitDiffTool();
export const gitAddTool = new GitAddTool();
export const gitCommitTool = new GitCommitTool();
export const gitCreateBranchTool = new GitCreateBranchTool();
export const gitPushTool = new GitPushTool();

