// Base class for our Git tools to handle schema definition correctly
abstract class GitTool extends Tool {
  // All tools will have a schema that parses a JSON string input
  schema = z.object({
    input: z.string().optional().transform(val => {
      if (!val) return {};
      try {
        return JSON.parse(val);
      } catch (e) {
        // If it's not a JSON string, treat it as a direct single argument if needed
        // For simplicity, we'll expect JSON for tools with args.
        return {};
      }
    })
  });
}

