const askHumanForHelpNode = async (state: AgentState): Promise<{ messages: BaseMessage[] }> => {
  const { messages } = state;
  const lastAiMessage = [...messages].reverse().find((m): m is AIMessage => m instanceof AIMessage);

  if (!lastAiMessage?.tool_calls) {
    throw new Error("No tool calls found in the last AI message for askHumanForHelpNode.");
  }

  const humanHelpToolCall = lastAiMessage.tool_calls.find(
    (toolCall) => toolCall.name === "askHumanForHelpTool"
  );

  if (!humanHelpToolCall) {
    throw new Error("No askHumanForHelpTool call found.");
  }

  const question = (humanHelpToolCall.args as any).question || "I need help. What should I do next?";

  const toolMessage = new ToolMessage({
    content: `Tool 'askHumanForHelpTool' called. The agent is asking for help.`,
    tool_call_id: humanHelpToolCall.id!,
  });

  const humanMessage = new HumanMessage({
      content: `Eira requires your input: ${question}`
  });

  return { messages: [toolMessage, humanMessage] };
};

